def lambda_handler(event, context):
    for record in event['Records']:
        message_id = record['messageId']
        body = record['body']

        if has_been_processed(message_id):
            print(f"Skipping duplicate message: {message_id}")
            continue

        process_message(body)
        mark_as_processed(message_id)

{
  "Version": "2012-10-17",
  "Statement": [
    {
      "Sid": "ManageUnicornRolesWithBoundary",
      "Effect": "Allow",
      "Action": [
        "iam:CreateRole",          // Allows creating a new IAM role
        "iam:AttachRolePolicy",    // Allows attaching managed policies to roles
        "iam:DetachRolePolicy"     // Allows detaching managed policies from roles
      ],
      // Resource restriction applies only to existing roles (Attach/Detach)
      "Resource": [
        "arn:aws:iam::*:role/unicorn*"  
      ],
      "Condition": {
        //when creating new role, resource does not exist . so user can create any role name . this restricts to prefix unicorn
        "StringLike": {
          "iam:RoleName": "unicorn*"
        },
        // Require a specific permission boundary when creating a role
        "StringEquals": {
          "iam:PermissionsBoundary": "arn:aws:iam::123456789012:policy/UnicornBoundaryPolicy"
        }
      }
    }
  ]
}
